mapper.insert:
INSERT INTO freeBoard(num, writer, email, subject, reg_date, readcount, ref, re_step, re_level, content, ip, upload) VALUES(freeBoard_num_seq.nextval, #{writer, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{subject}, sysdate, 0,
freeBoard_num_seq.nextval
#{ref}
, #{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip, jdbcType=VARCHAR}, #{upload, jdbcType=VARCHAR}
)

mapper.select:
SELECT
upload FROM freeBoard
WHERE num=#{num}

mapper.update:
UPDATE freeBoard
SET subject=#{subject}, email=#{email},
content=#{content}

	, upload=#{upload}

WHERE num=#{num}
