mapper.insert:
INSERT INTO noticeBoard(num, writer, email, subject, reg_date, readcount, ref, re_step, re_level, content, ip, upload) VALUES(noticeBoard_num_seq.nextval, #{writer, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{subject}, sysdate, 0,
noticeBoard_num_seq.nextval
#{ref}
, #{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip, jdbcType=VARCHAR}, #{upload, jdbcType=VARCHAR}
)

mapper.select:
SELECT
upload FROM noticeBoard
WHERE num=#{num}

mapper.update:
UPDATE noticeBoard
SET subject=#{subject}, email=#{email},
content=#{content}

	, upload=#{upload}

WHERE num=#{num}
